// src/Tabs.js
import React, { useState } from 'react';
import styled from 'styled-components';

const TabContainer = styled.div`
  display: flex;
  justify-content: center;
  background-color: #eee;
  padding-top: 50px;
  padding-bottom: 30px;
  border-radius: 10px 10px 0px 0px;
`;

const TabButton = styled.button`
  background: ${props => (props.active ? '#FF6F61' : 'transparent')};
  color: ${props => (props.active ? '#fff' : '#777')};
  border: 1px solid #aaa;
  height: 80px;
  box-shadow: ${props => (props.active ? '0px 0px 5px #fff' : 'transparent')};
  margin: 10px 30px;
  padding: 10px 20px;
  cursor: pointer;
  border-radius: 7px;
  font-size: 1rem;
  transition: background-color 0.3s;
  &:hover {
    background-color: #ff6f61;
    color: #fff;
  }
`;

const Content = styled.div`
  padding: 20px;
  background-color: #fff;
  color: #000;
  border-radius: 10px;
`;

const ItemsContainer = styled.div`
  display: grid;
  grid-template-columns: repeat(5, 1fr);
  grid-gap: 20px;
  margin-bottom: 20px;
`;

const ItemWrapper = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  text-align: center;
`;

const ItemIcon = styled.div`
  font-size: 2rem;
  margin-bottom: 10px;
`;

const ItemLabel = styled.div`
  font-size: 0.9rem;
`;

const categories = {
  'Fresh Produce': [
    'Fruits (apples, bananas, berries, etc.)',
    'Vegetables (leafy greens, root vegetables, etc.)',
    'Herbs (basil, cilantro, parsley, etc.)',
    'Organic options',
  ],
  'Pantry Staples': [
    'Grains (rice, quinoa, pasta, etc.)',
    'Legumes (beans, lentils, chickpeas, etc.)',
    'Cooking oils (olive oil, coconut oil, etc.)',
    'Baking essentials (flour, sugar, baking powder, etc.)',
    'Canned goods (tomatoes, beans, soups, etc.)',
  ],
  'Gourmet Treats': [
    'Artisanal chocolates',
    'Specialty cheeses',
    'Gourmet snacks (nuts, crackers, etc.)',
    'Imported delicacies (truffle oil, caviar, etc.)',
    'Premium condiments (gourmet mustards, sauces, etc.)',
  ],
  'Gluten-Free Snacks': [
    'Gluten-free chips and crackers',
    'Gluten-free granola bars',
    'Gluten-free cookies and sweets',
    'Gluten-free bread and baked goods',
    'Gluten-free cereals',
  ],
  'Plant-Based Alternatives': [
    'Plant-based milk (almond milk, oat milk, etc.)',
    'Meat substitutes (tofu, tempeh, seitan, etc.)',
    'Vegan cheese',
    'Plant-based protein powders',
    'Plant-based snacks (protein bars, chips, etc.)',
  ],
};

const Tabs = () => {
  const [activeTab, setActiveTab] = useState('Fresh Produce');

  return (
    <div>
      <TabContainer>
        {Object.keys(categories).map((category) => (
          <TabButton
            key={category}
            active={activeTab === category}
            onClick={() => setActiveTab(category)}
          >
            {category}
          </TabButton>
        ))}
      </TabContainer>
      <Content>
        {categories[activeTab].map((item, index) => (
          <div key={index} className="item">
            {item}
          </div>
        ))}
      </Content>
    </div>
  );
};

export default Tabs;
