// src/Tabs.js
import React, { useState } from 'react';
import styled from 'styled-components';

const TabContainer = styled.div`
  display: flex;
  justify-content: space-around;
  background-color: #eee;
  padding: 10px;
  border-radius: 10px;
`;

const TabButton = styled.button`
  background: ${props => (props.active ? '#FF6F61' : 'transparent')};
  color: ${props => (props.active ? '#fff' : '#aaa')};
  border: none;
  height: 80px;
  outline: none;
  padding: 10px 20px;
  cursor: pointer;
  border-radius: 5px;
  font-size: 1rem;
  transition: background-color 0.3s;

  &:hover {
    background-color: #FF6F61;
    color: #fff;
  }
`;

const Content = styled.div`
  padding: 20px;
  background-color: #eee;
  color: #fff;
  border-radius: 10px;
  margin-top: 10px;
`;

const Tabs = () => {
  const [activeTab, setActiveTab] = useState('dog-businesses');

  return (
    <div>
      <TabContainer>
        <TabButton active={activeTab === 'buy-a-dog'} onClick={() => setActiveTab('buy-a-dog')}>BUY A DOG</TabButton>
        <TabButton active={activeTab === 'dog-businesses'} onClick={() => setActiveTab('dog-businesses')}>DOG BUSINESSES</TabButton>
        <TabButton active={activeTab === 'dog-gadgets'} onClick={() => setActiveTab('dog-gadgets')}>DOG GADGETS</TabButton>
        <TabButton active={activeTab === 'dog-food'} onClick={() => setActiveTab('dog-food')}>DOG FOOD</TabButton>
      </TabContainer>
      <Content>
        {activeTab === 'buy-a-dog' && <div>Content for Buy a Dog</div>}
        {activeTab === 'dog-businesses' && <div>Content for Dog Businesses</div>}
        {activeTab === 'dog-gadgets' && <div>Content for Dog Gadgets</div>}
        {activeTab === 'dog-food' && <div>Content for Dog Food</div>}
      </Content>
    </div>
  );
};

export default Tabs;
